# encoding: utf-8

  class PopulateSakaiH2 < ActiveRecord::Base
    Rails.application.config.after_initialize do
      if Settings.campusdb.adapter == "h2"
        establish_connection "campusdb"
        sql = <<-SQL

        DROP TABLE IF EXISTS ANNOUNCEMENT_MESSAGE;
        -- Original Oracle DDL specifies LONG instead of CLOB, DATE instead of TIMESTAMP
        CREATE TABLE ANNOUNCEMENT_MESSAGE(
        "CHANNEL_ID" VARCHAR2(255),
        "MESSAGE_ID" VARCHAR2(36),
        "DRAFT" CHAR(1),
        "PUBVIEW" CHAR(1),
        "OWNER" VARCHAR2(99),
        "MESSAGE_DATE" TIMESTAMP,
        "XML" CLOB
        );

        DROP TABLE IF EXISTS SAKAI_PREFERENCES;
        -- Original Oracle table uses LONG instead of CLOB
        CREATE TABLE SAKAI_PREFERENCES(
        "PREFERENCES_ID" VARCHAR2(99),
        "XML" CLOB
        );

        -- Only includes the columns we use.
        DROP TABLE IF EXISTS SAKAI_SITE;
        CREATE TABLE SAKAI_SITE(
        "SITE_ID" VARCHAR2(99),
        "TITLE" VARCHAR2(99),
        "TYPE" VARCHAR2(99),
        "SHORT_DESC" CLOB,
        "DESCRIPTION" CLOB,
        "PUBLISHED" NUMBER(38)
        );

        DROP TABLE IF EXISTS SAKAI_SITE_PROPERTY;
        CREATE TABLE SAKAI_SITE_PROPERTY(
        "SITE_ID" VARCHAR2(99),
        "NAME" VARCHAR2(99),
        "VALUE" CLOB
        );

        DROP TABLE IF EXISTS SAKAI_SITE_TOOL;
        CREATE TABLE SAKAI_SITE_TOOL(
        "TOOL_ID" VARCHAR2(99),
        "PAGE_ID" VARCHAR2(99),
        "SITE_ID" VARCHAR2(99),
        "REGISTRATION" VARCHAR2(99),
        "PAGE_ORDER" NUMBER(38),
        "TITLE" VARCHAR2(99),
        "LAYOUT_HINTS" VARCHAR2(99)
        );

        DROP TABLE IF EXISTS SAKAI_SITE_USER;
        CREATE TABLE SAKAI_SITE_USER(
        "SITE_ID" VARCHAR2(99),
        "USER_ID" VARCHAR2(99),
        "PERMISSION" NUMBER(38)
        );

        DROP TABLE IF EXISTS SAKAI_USER_ID_MAP;
        CREATE TABLE SAKAI_USER_ID_MAP(
        "USER_ID" VARCHAR2(99),
        "EID" VARCHAR2(255)
        );

        DROP TABLE IF EXISTS SAKAI_REALM;
        CREATE TABLE SAKAI_REALM
          (
            REALM_KEY     NUMBER(38, 0),
            REALM_ID      VARCHAR2(300),
            PROVIDER_ID   VARCHAR2(1024),
            MAINTAIN_ROLE NUMBER(38, 0),
            CREATEDBY     VARCHAR2(99),
            MODIFIEDBY    VARCHAR2(99),
            CREATEDON TIMESTAMP,
            MODIFIEDON TIMESTAMP
          );

        DROP TABLE IF EXISTS SAKAI_REALM_RL_GR;
        CREATE TABLE SAKAI_REALM_RL_GR
          (
            REALM_KEY NUMBER(38, 0),
            USER_ID   VARCHAR2(99),
            ROLE_KEY  NUMBER(38, 0),
            ACTIVE    CHAR(1),
            PROVIDED  CHAR(1)
          );

